package com.syntax.class08_9_11_2022;

public class practice4myself {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int m=1; m<=4; m++) {
			for(int k=1; k<=3; k++) {
				System.out.print("* ");
			}
			System.out.println();
		}
		
		int i=1;
		do {System.out.println(i);
		i++;
			
		
	}while(i<=10);
		
		
		//nested
for (int j=0; j<5; j++) {
			
			int a=0;
			while(a<3) {
				System.out.println(a);
				a++;
			}
		}
		
		
	}
}

